package com.example.frontend_alp_vp.navigation

import androidx.compose.runtime.Composable
import androidx.navigation.NavHostController
import androidx.navigation.compose.NavHost
import androidx.navigation.compose.composable
import com.example.frontend_alp_vp.ui.view.home.HomePage
import com.example.frontend_alp_vp.ui.view.kuliner.KulinerDetail
import com.example.frontend_alp_vp.ui.view.souvenir.SouvenirPage
import com.example.frontend_alp_vp.ui.view.wisata.WisataPage
import com.example.frontend_alp_vp.ui.view.review.ReviewPage

@Composable
fun NavGraph(navController: NavHostController) {
    NavHost(
        navController = navController,
        startDestination = Screen.Home.route
    ) {
        composable(route = Screen.Home.route) {
            HomePage(
                onNavigateToKuliner = { navController.navigate(Screen.KulinerDetail.route) },
                onNavigateToWisata = { navController.navigate(Screen.Wisata.route) },
                onNavigateToSouvenir = { navController.navigate(Screen.Souvenir.route) }
            )
        }

        composable(route = Screen.KulinerDetail.route) {
            KulinerDetail(
                onBackClick = { navController.popBackStack() },
                onAddReviewClick = { navController.navigate(Screen.AddReview.route) }
            )
        }

        composable(route = Screen.Wisata.route) {
            WisataPage(
                onNavigateBack = { navController.popBackStack() },
                onNavigateToDetail = { placeId ->
                    navController.navigate(Screen.PlaceDetail.createRoute(placeId))
                }
            )
        }

        composable(route = Screen.Souvenir.route) {
            SouvenirPage(
                onNavigateBack = { navController.popBackStack() },
                onNavigateToDetail = { placeId ->
                    navController.navigate(Screen.PlaceDetail.createRoute(placeId))
                }
            )
        }

        composable(
            route = Screen.PlaceDetail.route,
            arguments = Screen.PlaceDetail.arguments
        ) { backStackEntry ->
            val placeId = backStackEntry.arguments?.getInt(Screen.PlaceDetail.PLACE_ID_ARG) ?: 0
            KulinerDetail(
                placeId = placeId,
                onBackClick = { navController.popBackStack() },
                onAddReviewClick = { navController.navigate(Screen.AddReview.route) }
            )
        }

        composable(route = Screen.AddReview.route) {
            ReviewPage(
                onBackClick = { navController.popBackStack() },
                onReviewSubmitted = { navController.popBackStack() }
            )
        }
    }
}

package com.example.frontend_alp_vp.ui.viewmodel

import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import com.example.frontend_alp_vp.ui.model.PlaceModel
import com.example.frontend_alp_vp.ui.model.PlaceCategoryModel
import com.example.frontend_alp_vp.ui.repository.PlaceRepository
import com.example.frontend_alp_vp.ui.repository.PlaceCategoryRepository
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.flow.asStateFlow
import kotlinx.coroutines.launch

class HomeViewModel(
    private val placeRepository: PlaceRepository,
    private val categoryRepository: PlaceCategoryRepository
) : ViewModel() {

    private val _places = MutableStateFlow<List<PlaceModel>>(emptyList())
    val places: StateFlow<List<PlaceModel>> = _places.asStateFlow()

    private val _categories = MutableStateFlow<List<PlaceCategoryModel>>(emptyList())
    val categories: StateFlow<List<PlaceCategoryModel>> = _categories.asStateFlow()

    private val _isLoading = MutableStateFlow(false)
    val isLoading: StateFlow<Boolean> = _isLoading.asStateFlow()

    private val _error = MutableStateFlow<String?>(null)
    val error: StateFlow<String?> = _error.asStateFlow()

    init {
        loadHomeData()
    }

    fun loadHomeData() {
        viewModelScope.launch {
            _isLoading.value = true
            try {
                val placesList = placeRepository.getAllPlaces()
                val categoriesList = categoryRepository.getAllCategories()
                _places.value = placesList
                _categories.value = categoriesList
            } catch (e: Exception) {
                _error.value = e.message ?: "Failed to load home data"
            } finally {
                _isLoading.value = false
            }
        }
    }

    fun clearError() {
        _error.value = null
    }
}
